export function Validate(target: any, propertyKey: string, parameterIndex: number) {
  const existingParams = Reflect.getMetadata('validate_params', target, propertyKey) || [];
  existingParams.push(parameterIndex);
  Reflect.defineMetadata('validate_params', existingParams, target, propertyKey);
}`,benefits:["angular.architecture.decorator.benefit1","angular.architecture.decorator.benefit2","angular.architecture.decorator.benefit3","angular.architecture.decorator.benefit4"],useCases:["angular.architecture.decorator.useCase1","angular.architecture.decorator.useCase2","angular.architecture.decorator.useCase3"],implementation:["angular.architecture.decorator.implementation1","angular.architecture.decorator.implementation2","angular.architecture.decorator.implementation3","angular.architecture.decorator.implementation4"],challenges:["angular.architecture.decorator.challenge1","angular.architecture.decorator.challenge2"]},{title:"angular.architecture.strategy.title",description:"angular.architecture.strategy.description",icon:"psychology",detailTitle:"angular.architecture.strategy.detailTitle",explanation:"angular.architecture.strategy.explanation",codeExample:`// Strategy Interface
export interface ValidationStrategy {
  validate(value: any): boolean;
  getErrorMessage(): string;
}

// Concrete Strategies
export class EmailValidationStrategy implements ValidationStrategy {
  validate(value: string): boolean {
    return /^[^\\s@]+@[^\\s@]+\\.[^\\s@]+$/.test(value);
  }
  
  getErrorMessage(): string {
    return 'Invalid email format';
  }
}

export class PhoneValidationStrategy implements ValidationStrategy {
  validate(value: string): boolean {
    return /^\\+?[1-9]\\d{1,14}$/.test(value);
  }
  
  getErrorMessage(): string {
    return 'Invalid phone number';
  }
}

// Context
@Injectable({ providedIn: 'root' })
export class ValidationService {
  private strategy!: ValidationStrategy;

  setStrategy(strategy: ValidationStrategy) {
    this.strategy = strategy;
  }

  validate(value: any): { valid: boolean; error?: string } {
    const valid = this.strategy.validate(value);
    return {
      valid,
      error: valid ? undefined : this.strategy.getErrorMessage()
    };
  }
}

