@Directive({
  selector: '[appFocusTrap]',
  standalone: true
})
export class FocusTrapDirective implements AfterViewInit, OnDestroy {
  private elementRef = inject(ElementRef);
  private focusableElements: HTMLElement[] = [];
  private firstFocusable?: HTMLElement;
  private lastFocusable?: HTMLElement;
  
  ngAfterViewInit() {
    this.updateFocusableElements();
    this.firstFocusable?.focus();
    
    document.addEventListener('keydown', this.handleKeyDown);
  }
  
  private updateFocusableElements() {
    const selectors = [
      'a[href]',
      'button:not([disabled])',
      'textarea:not([disabled])',
      'input:not([disabled])',
      'select:not([disabled])',
      '[tabindex]:not([tabindex="-1"])'
    ].join(', ');
    
    this.focusableElements = Array.from(
      this.elementRef.nativeElement.querySelectorAll(selectors)
    );
    
    this.firstFocusable = this.focusableElements[0];
    this.lastFocusable = this.focusableElements[
      this.focusableElements.length - 1
    ];
  }
  
  private handleKeyDown = (event: KeyboardEvent) => {
    if (event.key !== 'Tab') return;
    
    const activeElement = document.activeElement as HTMLElement;
    
    if (event.shiftKey) {
      // Shift + Tab
      if (activeElement === this.firstFocusable) {
        event.preventDefault();
        this.lastFocusable?.focus();
      }
    } else {
      // Tab
      if (activeElement === this.lastFocusable) {
        event.preventDefault();
        this.firstFocusable?.focus();
      }
    }
  };
  
  ngOnDestroy() {
    document.removeEventListener('keydown', this.handleKeyDown);
  }
}

// Auto Focus Directive
@Directive({
  selector: '[appAutoFocus]',
  standalone: true
})
export class AutoFocusDirective implements AfterViewInit {
  private elementRef = inject(ElementRef);
  
  @Input() appAutoFocus = true;
  @Input() focusDelay = 0;
  
  ngAfterViewInit() {
    if (this.appAutoFocus) {
      setTimeout(() => {
        this.elementRef.nativeElement.focus();
      }, this.focusDelay);
    }
  }
}

// Focus Management Service
@Injectable({ providedIn: 'root' })
export class FocusManagerService {
  private previousFocus?: HTMLElement;
  
  saveFocus() {
    this.previousFocus = document.activeElement as HTMLElement;
  }
  
  restoreFocus() {
    if (this.previousFocus) {
      this.previousFocus.focus();
      this.previousFocus = undefined;
    }
  }
  
  focusFirst(container: HTMLElement) {
    const firstFocusable = container.querySelector<HTMLElement>(
      'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
    );
    firstFocusable?.focus();
  }
}

